import React from 'react';
import './Testimonials.css';

const Testimonials = ({ theme }) => {
  const getTestimonialContent = () => {
    switch (theme) {
      case 'Mafia':
        return 'Our clients trust us to protect their business and expand their territory.';
      case 'Drug Game':
        return 'Our customers know we’re the plug for high-quality digital services.';
      case 'Adult Entertainment':
        return 'We keep our clients’ audiences entertained and coming back for more.';
      case 'Monopoly':
        return 'We help businesses dominate their industries with strategic solutions.';
      case 'Foodie Influencer':
        return 'Our clients savor every bite of our delicious digital solutions.';
      case 'Social Media Influencer':
        return 'Our clients go viral with our top-tier digital strategies.';
      default:
        return 'Hear what our satisfied clients have to say about our services.';
    }
  };

  return (
    <section
      className={`testimonials ${theme.toLowerCase()}`}
      data-aos="fade-up"
    >
      <h2>Client Testimonials</h2>
      <p>{getTestimonialContent()}</p>
    </section>
  );
};

export default Testimonials;
