import React, {
  createContext,
  useContext,
  useState,
  useEffect,
} from 'react';
import ThemeSelectionModal from '../components/ThemeSelectionModal/ThemeSelectionModal'; // Import your modal component

const ThemeContext = createContext();

export const useTheme = () => useContext(ThemeContext);

export const ThemeProvider = ({ children }) => {
  // Function to normalize theme keys
  const normalizeThemeKey = (theme) =>
    theme.replace(/\s+/g, '').toLowerCase();

  // Modal visibility state
  const [showModal, setShowModal] = useState(false);

  // Theme state
  const [theme, setTheme] = useState(null);

  // Check if a theme is already set in localStorage when the component mounts
  useEffect(() => {
    const savedTheme = localStorage.getItem('selectedTheme');
    console.log('Saved theme:', savedTheme);
    if (savedTheme) {
      setTheme(normalizeThemeKey(savedTheme)); // Normalize theme key
    } else {
      setShowModal(true); // Show modal if no theme is saved
    }
  }, []); // Empty dependency array ensures this effect only runs once on mount

  useEffect(() => {
    console.log('Current theme from context:', theme);
  }, [theme]);

  const handleSetTheme = (newTheme) => {
    const normalizedTheme = normalizeThemeKey(newTheme);
    console.log('Setting theme in localStorage:', normalizedTheme);
    localStorage.setItem('selectedTheme', normalizedTheme);
    setTheme(normalizedTheme);
    setShowModal(false); // Hide modal after theme is selected
  };

  // If no theme is selected, show the selection modal
  if (showModal) {
    return <ThemeSelectionModal setTheme={handleSetTheme} />;
  }

  return (
    <ThemeContext.Provider
      value={{ theme, setTheme: handleSetTheme }}
    >
      {children}
    </ThemeContext.Provider>
  );
};
