import React, { useEffect } from 'react';
import { useTheme } from './context/ThemeContext';
import Home from './pages/Home/Home';
import ThemeSelectionModal from './components/ThemeSelectionModal/ThemeSelectionModal';
import AOS from 'aos';
import 'aos/dist/aos.css';

function App() {
  const { theme, setTheme } = useTheme();

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  // If theme is not set, render the theme selection modal
  if (!theme) {
    console.log('No theme selected, showing ThemeSelectionModal');
    return <ThemeSelectionModal setTheme={setTheme} />;
  }

  return (
    <div className={`app ${theme}`}>
      <Home />
    </div>
  );
}

export default App;
