import './HeroSection.css';
import ContactFormModal from '../ContactFormModal/ContactFormModal';
import { useThemeSettings } from '../../context/theme/ThemeSettingsContext/ThemeSettingsContext';
import { useState } from 'react';

const HeroSection = ({
  title,
  description,
  align = 'center',
  buttons = [],
  backgroundType = 'image', // 'image' or 'video'
  backgroundSrc = '/assets/videos/logovideo.mp4', // default video source
}) => {
  const themeSettings = useThemeSettings();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleModalOpen = () => {
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  return (
    <section
      className={`hero-section ${align}`}
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
    >
      {/* Background element based on the backgroundType */}
      {backgroundType === 'video' ? (
        <video className="background-video" autoPlay loop muted>
          <source src={backgroundSrc} type="video/mp4" />
        </video>
      ) : (
        <div
          className="background-image"
          style={{ backgroundImage: `url(${backgroundSrc})` }}
        ></div>
      )}

      <div className="hero-content">
        <h1>{title}</h1>
        <p>{description}</p>
        <div className="hero-buttons">
          {buttons.map((button, index) => (
            <button
              key={index}
              onClick={
                button.label === 'Get Started'
                  ? handleModalOpen
                  : () => (window.location.href = button.href)
              }
              className="hero-button"
              style={{ backgroundColor: themeSettings.colors.accent }}
            >
              {button.label}
            </button>
          ))}
        </div>
      </div>
      {isModalOpen && <ContactFormModal onClose={handleModalClose} />}
    </section>
  );
};

export default HeroSection;
