import React, { useState } from 'react';
import './CalculatorWidget.css';
import { useThemeSettings } from '../context/ThemeSettingsContext';
import BasicCalculator from './BasicCalculator.js';
import MortgageCalculator from './MortgageCalculator.js';
import TipCalculator from './TipCalculator.js';
import LoanCalculator from './LoanCalculator.js';
import InvestorDealCalculator from './InvestorDealCalculator.js';

const CalculatorWidget = () => {
  const [mode, setMode] = useState('basic'); // Default mode
  const themeSettings = useThemeSettings();

  const renderCalculator = () => {
    switch (mode) {
      case 'basic':
        return <BasicCalculator />;
      case 'mortgage':
        return <MortgageCalculator />;
      case 'tips':
        return <TipCalculator />;
      case 'loan':
        return <LoanCalculator />;
      case 'investorDeal':
        return <InvestorDealCalculator />;
      default:
        return <BasicCalculator />;
    }
  };

  return (
    <div
      className="calculator-widget"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
    >
      <h2
        style={{
          color: themeSettings.colors.accent,
          borderBottom: `2px solid ${themeSettings.colors.accent}`,
        }}
      >
        Multi-Function Calculator
      </h2>
      <div className="mode-selector">
        <button onClick={() => setMode('basic')}>Basic</button>
        <button onClick={() => setMode('mortgage')}>Mortgage</button>
        <button onClick={() => setMode('tips')}>Tips</button>
        <button onClick={() => setMode('loan')}>Loans</button>
        <button onClick={() => setMode('investorDeal')}>
          Investor Deal
        </button>
      </div>
      {renderCalculator()}
    </div>
  );
};
export default CalculatorWidget;
