import './CalculatorWidget.css';
import BasicCalculator from './BasicCalculator';
import InvestorDealCalculator from './InvestorDealCalculator';
import LoanCalculator from './LoanCalculator';
import MortgageCalculator from './MortgageCalculator';
import TipCalculator from './TipCalculator';
import { useState } from 'react';
import { useThemeSettings } from '../../context/theme/ThemeSettingsContext/ThemeSettingsContext';

const CalculatorWidget = () => {
  const [mode, setMode] = useState('basic'); // Default mode
  const themeSettings = useThemeSettings();

  const renderCalculator = () => {
    switch (mode) {
      case 'basic':
        return <BasicCalculator />;
      case 'mortgage':
        return <MortgageCalculator />;
      case 'tips':
        return <TipCalculator />;
      case 'loan':
        return <LoanCalculator />;
      case 'investorDeal':
        return <InvestorDealCalculator />;
      default:
        return <BasicCalculator />;
    }
  };

  return (
    <div
      className="calculator-widget"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
      role="region"
      aria-label="Multi-Function Calculator Widget"
    >
      <h2
        style={{
          color: themeSettings.colors.accent,
          borderBottom: `2px solid ${themeSettings.colors.accent}`,
        }}
      >
        Multi-Function Calculator
      </h2>
      <div className="mode-selector">
        {['basic', 'mortgage', 'tips', 'loan', 'investorDeal'].map(
          (type) => (
            <button
              key={type}
              onClick={() => setMode(type)}
              style={{
                backgroundColor:
                  mode === type
                    ? themeSettings.colors.accent
                    : themeSettings.colors.secondaryBackground,
                color:
                  mode === type
                    ? themeSettings.colors.textOnAccent
                    : themeSettings.colors.text,
              }}
              className={`mode-button ${
                mode === type ? 'active' : ''
              }`}
            >
              {type.charAt(0).toUpperCase() + type.slice(1)}
            </button>
          )
        )}
      </div>
      <div className="calculator-content">{renderCalculator()}</div>
    </div>
  );
};

export default CalculatorWidget;
