import React, {
  createContext,
  useContext,
  useState,
  useEffect,
} from 'react';

const ThemeContext = createContext();

export const useTheme = () => useContext(ThemeContext);

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState(null); // Start with null to ensure fallback

  useEffect(() => {
    const savedTheme = localStorage.getItem('selectedTheme');
    console.log('Retrieved theme from localStorage:', savedTheme);
    if (savedTheme) {
      setTheme(savedTheme);
    } else {
      // If no theme is saved, fallback to a default theme
      setTheme('Drug Game');
    }
  }, []);

  const handleSetTheme = (newTheme) => {
    console.log('Setting theme in localStorage:', newTheme);
    localStorage.setItem('selectedTheme', newTheme);
    setTheme(newTheme);
  };

  // Render children only when the theme is set
  if (!theme) {
    return null; // Don't render anything until the theme is set
  }

  return (
    <ThemeContext.Provider
      value={{ theme, setTheme: handleSetTheme }}
    >
      {children}
    </ThemeContext.Provider>
  );
};
