import React, { useState } from 'react';
import './CalculatorWidget.css';

const CalculatorWidget = () => {
  const [displayValue, setDisplayValue] = useState('0');
  const [operator, setOperator] = useState(null);
  const [waitingForOperand, setWaitingForOperand] = useState(false);
  const [value, setValue] = useState(null);

  const inputDigit = (digit) => {
    if (waitingForOperand) {
      setDisplayValue(digit);
      setWaitingForOperand(false);
    } else {
      setDisplayValue(
        displayValue === '0' ? digit : displayValue + digit
      );
    }
  };

  const inputDot = () => {
    if (!displayValue.includes('.')) {
      setDisplayValue(displayValue + '.');
    }
  };

  const clearDisplay = () => {
    setDisplayValue('0');
  };

  const toggleSign = () => {
    setDisplayValue((prevValue) =>
      prevValue.charAt(0) === '-'
        ? prevValue.slice(1)
        : '-' + prevValue
    );
  };

  const handleOperator = (nextOperator) => {
    const inputValue = parseFloat(displayValue);

    if (value == null) {
      setValue(inputValue);
    } else if (operator) {
      const currentValue = value || 0;
      const newValue =
        operator === '+'
          ? currentValue + inputValue
          : operator === '-'
          ? currentValue - inputValue
          : operator === '*'
          ? currentValue * inputValue
          : currentValue / inputValue;

      setValue(newValue);
      setDisplayValue(String(newValue));
    }

    setWaitingForOperand(true);
    setOperator(nextOperator);
  };

  const inputPercent = () => {
    const currentValue = parseFloat(displayValue);

    if (currentValue === 0) return;

    const fixedDigits = displayValue.replace(/^-?\d*\.?/, '');
    const newValue = parseFloat(displayValue) / 100;

    setDisplayValue(String(newValue.toFixed(fixedDigits.length + 2)));
  };

  const performOperation = () => {
    handleOperator(operator);
  };

  return (
    <div className="calculator-widget">
      <div className="calculator-display">{displayValue}</div>
      <div className="calculator-keypad">
        <button
          className="calculator-key function-key"
          onClick={clearDisplay}
        >
          AC
        </button>
        <button
          className="calculator-key function-key"
          onClick={toggleSign}
        >
          ±
        </button>
        <button
          className="calculator-key function-key"
          onClick={inputPercent}
        >
          %
        </button>
        <button
          className="calculator-key operator-key"
          onClick={() => handleOperator('/')}
        >
          ÷
        </button>

        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('7')}
        >
          7
        </button>
        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('8')}
        >
          8
        </button>
        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('9')}
        >
          9
        </button>
        <button
          className="calculator-key operator-key"
          onClick={() => handleOperator('*')}
        >
          ×
        </button>

        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('4')}
        >
          4
        </button>
        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('5')}
        >
          5
        </button>
        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('6')}
        >
          6
        </button>
        <button
          className="calculator-key operator-key"
          onClick={() => handleOperator('-')}
        >
          −
        </button>

        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('1')}
        >
          1
        </button>
        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('2')}
        >
          2
        </button>
        <button
          className="calculator-key number-key"
          onClick={() => inputDigit('3')}
        >
          3
        </button>
        <button
          className="calculator-key operator-key"
          onClick={() => handleOperator('+')}
        >
          +
        </button>

        <button
          className="calculator-key number-key zero-key"
          onClick={() => inputDigit('0')}
        >
          0
        </button>
        <button
          className="calculator-key number-key"
          onClick={inputDot}
        >
          .
        </button>
        <button
          className="calculator-key operator-key"
          onClick={performOperation}
        >
          =
        </button>
      </div>
    </div>
  );
};

export default CalculatorWidget;
