import React from 'react';
import { ThemeProvider } from './context/ThemeContext'; // Ensure the correct path
import ThemeSelectionModal from './components/ThemeSelectionModal/ThemeSelectionModal';
import Home from './pages/Home/Home';
import AOS from 'aos';
import 'aos/dist/aos.css';
import './App.css';

function App() {
  const [showModal, setShowModal] = React.useState(false);

  React.useEffect(() => {
    const selectedTheme = localStorage.getItem('selectedTheme');
    if (!selectedTheme) {
      setShowModal(true);
    }
    AOS.init();
  }, []);

  const handleThemeSelection = (theme) => {
    localStorage.setItem('selectedTheme', theme);
    setShowModal(false);
  };

  return (
    <ThemeProvider>
      <div className="App">
        {showModal && (
          <ThemeSelectionModal setTheme={handleThemeSelection} />
        )}
        {!showModal && <Home />}
      </div>
    </ThemeProvider>
  );
}

export default App;
