import React, { useState } from 'react';
import { useThemeSettings } from '../../context/theme/ThemeSettingsContext/ThemeSettingsContext';
import './APIWidgets.css';

const APIWidgets = () => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const themeSettings = useThemeSettings();
  const apiKey = '2eaf366e4ec243d68c034719242308';

  const fetchWeather = () => {
    setLoading(true);
    setError(null);

    fetch(
      `https://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=dallas&days=3&aqi=no&alerts=no`
    )
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch weather data');
        }
        return response.json();
      })
      .then((data) => {
        setWeather(data);
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to fetch weather data');
        setLoading(false);
      });
  };

  const convertToFahrenheit = (celsius) => {
    return (celsius * 9) / 5 + 32;
  };

  return (
    <div
      className="api-widgets"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
    >
      <h2
        style={{
          color: themeSettings.colors.accent,
          borderBottom: `2px solid ${themeSettings.colors.accent}`,
        }}
      >
        Weather in Fort Worth
      </h2>
      <button
        onClick={fetchWeather}
        className="fetch-button"
        style={{
          backgroundColor: themeSettings.colors.accent,
          color: themeSettings.colors.textOnAccent,
        }}
      >
        Get Weather
      </button>
      {loading ? (
        <div className="loading-spinner"></div>
      ) : error ? (
        <p className="error">{error}</p>
      ) : weather ? (
        <div className="weather-info">
          <div className="current-weather">
            <h3>Current Conditions</h3>
            <img
              src={weather.current.condition.icon}
              alt={weather.current.condition.text}
            />
            <p>
              Temperature:{' '}
              {convertToFahrenheit(weather.current.temp_c)}°F
            </p>
            <p>Condition: {weather.current.condition.text}</p>
            <p>Wind: {weather.current.wind_mph} mph</p>
            <p>Humidity: {weather.current.humidity}%</p>
          </div>
          <div className="forecast">
            <h3>3-Day Forecast</h3>
            {weather.forecast.forecastday.map((day, index) => (
              <div
                key={index}
                className="forecast-day"
                data-aos="fade-up"
              >
                <p>{day.date}</p>
                <img
                  src={day.day.condition.icon}
                  alt={day.day.condition.text}
                />
                <p>Max: {convertToFahrenheit(day.day.maxtemp_c)}°F</p>
                <p>Min: {convertToFahrenheit(day.day.mintemp_c)}°F</p>
                <p>{day.day.condition.text}</p>
              </div>
            ))}
          </div>
        </div>
      ) : null}
    </div>
  );
};

export default APIWidgets;
