import React from 'react';
import './HeroSection.css';
import { useThemeSettings } from '../../context/theme/ThemeSettingsContext/ThemeSettingsContext';
import CTAButton from '../CTAButton/CTAButton'; // Import reusable CTAButton component
import { useNavigate } from 'react-router-dom'; // Assuming you are using react-router for navigation

const HeroSection = ({
  title,
  subtitle,
  backgroundVideo,
  align = 'center',
  overlayOpacity = 0.5, // Default opacity
}) => {
  const themeSettings = useThemeSettings();
  const navigate = useNavigate(); // Initialize the navigation hook

  const handleGetStarted = () => {
    navigate('/home'); // Replace '/start' with the appropriate route for starting the user journey
  };

  return (
    <section
      className={`hero-section ${align}`}
      style={{
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
    >
      {backgroundVideo && (
        <video
          className="background-video"
          autoPlay
          loop
          muted
          playsInline
        >
          <source src={backgroundVideo} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}
      <div
        className="hero-overlay"
        style={{
          backgroundColor: `rgba(0, 0, 0, ${overlayOpacity})`,
        }}
      ></div>
      <div className="hero-content">
        <h1>{title}</h1>
        <p>{subtitle}</p>
        <div className="hero-buttons">
          {/* Use the reusable CTAButton component */}
          <CTAButton text="Enter" onClick={handleGetStarted} />
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
