import React, { useState } from 'react';
import './MysteryBoxSection.css';
import Confetti from 'react-confetti';

const MysteryBoxSection = () => {
  const [isRevealed, setIsRevealed] = useState(false);

  const handleRevealClick = () => {
    setIsRevealed(true);
  };

  return (
    <section className="mystery-box-section">
      {isRevealed && <Confetti gravity={1.2} numberOfPieces={500} />}
      <div className="mystery-box-content">
        {!isRevealed ? (
          <div className="gift-box" onClick={handleRevealClick}>
            <div className="box-lid"></div>
            <div className="box-base"></div>
            <div className="ribbon-horizontal"></div>
            <div className="ribbon-vertical"></div>
            <div className="bow">
              <div className="bow-left"></div>
              <div className="bow-right"></div>
              <div className="knot"></div>
            </div>
            <p className="prompt-text">Click to Open</p>
          </div>
        ) : (
          <div className="revealed-content">
            <h2>Unlock Your Potential with TechSaaS Vision</h2>
            <p>
              Congratulations! Youâ€™ve taken the first step towards
              unlocking a brighter future for your business. Explore
              the services and opportunities that await you.
            </p>
            <a href="/services" className="cta-button">
              Explore Services
            </a>
          </div>
        )}
      </div>
    </section>
  );
};

export default MysteryBoxSection;
