import React, { useState } from 'react';
import './MysteryBox.css';
import Confetti from 'react-confetti';
import CTAButton from '../CTAButton/CTAButton';

const MysteryBox = ({ onReveal }) => {
  const [isOpened, setIsOpened] = useState(false);

  const handleClick = () => {
    setIsOpened(true);
    if (onReveal) {
      onReveal();
    }
  };

  return (
    <div className="mystery-box-container">
      {!isOpened ? (
        <div className="mystery-box" onClick={handleClick}>
          <div className="box">
            <div className="lid"></div>
            <div className="ribbon"></div>
            <div className="ribbon-cross"></div>
          </div>
          <div className="cta-text">
            <CTAButton text="Open the Mystery Box" />
          </div>
        </div>
      ) : (
        <>
          <Confetti numberOfPieces={300} recycle={false} />
          <div className="cta-text">
            <CTAButton text="Explore More" link="/services" />
          </div>
        </>
      )}
    </div>
  );
};

export default MysteryBox;
