import React from 'react';
import SectionHeader from '../../components/SectionHeader/SectionHeader';
import ServiceCard from '../../components/ServiceCard/ServiceCard';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
import { ReactComponent as WebDevelopmentIcon } from '../../assets/icons/web-development-icon.svg';
import { ReactComponent as LandingPageIcon } from '../../assets/icons/landing-page-icon.svg';
import { ReactComponent as SeoIcon } from '../../assets/icons/seo-icon.svg';
import { ReactComponent as EcommerceIcon } from '../../assets/icons/ecommerce-icon.svg';
import { ReactComponent as AdvancedLandingPageIcon } from '../../assets/icons/advanced-landing-page-icon.svg';
import { ReactComponent as WebAppIcon } from '../../assets/icons/web-app-icon.svg';
import { ReactComponent as EnterpriseWebIcon } from '../../assets/icons/enterprise-web-icon.svg';
import { ReactComponent as SaasIcon } from '../../assets/icons/saas-icon.svg';
import { ReactComponent as DigitalTransformationIcon } from '../../assets/icons/digital-transformation-icon.svg';
import AOS from 'aos';
import 'aos/dist/aos.css';

import './ServicesPage.css';

const servicesData = [
  {
    tier: 'Low Tier',
    services: [
      {
        title: 'Basic Website Development',
        description:
          'Simple 3-5 page static website with responsive design and basic contact form.',
        metric: '$1,000',
        IconComponent: WebDevelopmentIcon,
      },
      {
        title: 'Basic Landing Page',
        description:
          'Single-page landing site with simple form capture and minimal animations.',
        metric: '$800',
        IconComponent: LandingPageIcon,
      },
      {
        title: 'Basic SEO Setup',
        description:
          'On-page SEO optimization and submission to Google Search Console.',
        metric: '$500',
        IconComponent: SeoIcon,
      },
    ],
  },
  {
    tier: 'Mid Tier',
    services: [
      {
        title: 'Standard Website Development',
        description:
          '5-10 pages with CMS integration, basic e-commerce, and SEO.',
        metric: '$4,500',
        IconComponent: WebDevelopmentIcon,
      },
      {
        title: 'Advanced Landing Page',
        description:
          'Optimized for conversions, integrated with CRM, and advanced animations.',
        metric: '$3,000',
        IconComponent: AdvancedLandingPageIcon,
      },
      {
        title: 'Web Application Development',
        description:
          'Full-stack web application with user authentication and dashboard.',
        metric: '$6,000',
        IconComponent: WebAppIcon,
      },
    ],
  },
  {
    tier: 'High Tier',
    services: [
      {
        title: 'Enterprise Website Development',
        description:
          '20+ pages with fully customized design, advanced e-commerce, and SEO.',
        metric: '$15,000',
        IconComponent: EnterpriseWebIcon,
      },
      {
        title: 'Custom SaaS Development',
        description:
          'Full-featured SaaS platform with API integration and advanced user roles.',
        metric: '$20,000+',
        IconComponent: SaasIcon,
      },
      {
        title: 'Digital Transformation Consulting',
        description:
          'Full business process automation and ongoing support.',
        metric: '$25,000+',
        IconComponent: DigitalTransformationIcon,
      },
    ],
  },
];

const ServicesPage = () => {
  const themeSettings = useThemeSettings();

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  return (
    <div
      className="services-page"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
    >
      <SectionHeader
        title="Our Services"
        subtitle="Explore the wide range of services we offer, tailored to meet your needs."
      />
      <div className="services-overview">
        {servicesData.map((tier, index) => (
          <div
            key={index}
            className={`tier-section ${tier.tier
              .toLowerCase()
              .replace(' ', '-')}`}
          >
            <h3
              className="tier-title"
              style={{ color: themeSettings.colors.primary }}
            >
              {tier.tier}
            </h3>
            <div className="service-cards">
              {tier.services.map((service, serviceIndex) => (
                <ServiceCard
                  key={serviceIndex}
                  title={service.title}
                  description={service.description}
                  metric={service.metric}
                  IconComponent={service.IconComponent}
                  badge={
                    tier.tier === 'Mid Tier' ? 'Most Popular' : null
                  } // Add badge for mid-tier
                />
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* Special CTA Banner for Mid Tier */}
      <div className="mid-tier-cta-banner">
        <h3>
          Looking for the best value? Our Mid-Tier Package offers the
          perfect balance of features and cost.
        </h3>
        <button className="cta-button">Get Started</button>
      </div>
    </div>
  );
};

export default ServicesPage;
