import React from 'react';
import './ThemeSelectionModal.css';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
const ThemeSelectionModal = ({ setTheme }) => {
  const themeSettings = useThemeSettings();

  // Extract the theme objects
  const themes = Object.values(themeSettings);

  const handleThemeSelection = (selectedTheme) => {
    setTheme(selectedTheme); // Set the selected theme
  };

  return (
    <div className="theme-selection-modal">
      <h2>Select Your Theme</h2>
      <div className="theme-options">
        {themes.map((theme) => (
          <button
            key={theme.id} // Use `id` as the unique key
            onClick={() => handleThemeSelection(theme.name)} // Set the theme by its name
            className="theme-button"
          >
            {theme.name} {/* Display the theme name */}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ThemeSelectionModal;
