import React, {
  createContext,
  useContext,
  useState,
  useEffect,
} from 'react';

const ThemeContext = createContext();

export const useTheme = () => useContext(ThemeContext);

export const ThemeProvider = ({ children }) => {
  // Initialize the theme with a function that checks localStorage
  const [theme, setTheme] = useState(() => {
    const savedTheme = localStorage.getItem('selectedTheme');
    return savedTheme || 'default'; // Fallback to 'default' if no theme is found
  });

  // Update localStorage whenever the theme changes
  useEffect(() => {
    if (theme) {
      console.log('Setting theme in localStorage:', theme);
      localStorage.setItem('selectedTheme', theme);
    }
  }, [theme]); // Effect runs whenever the theme changes

  const handleSetTheme = (newTheme) => {
    console.log('Changing theme:', newTheme);
    setTheme(newTheme);
  };

  return (
    <ThemeContext.Provider
      value={{ theme, setTheme: handleSetTheme }}
    >
      {children}
    </ThemeContext.Provider>
  );
};
