import React, { useState } from 'react';
import './APIWidgets.css';

const APIWidgets = () => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const apiKey = '2eaf366e4ec243d68c034719242308';

  const fetchWeather = () => {
    setLoading(true);
    setError(null);
    fetch(
      `https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=FortWorth` // Fort Worth, TX
    )
      .then((response) => response.json())
      .then((data) => {
        setWeather(data);
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to fetch weather data');
        setLoading(false);
      });
  };

  return (
    <div className="api-widgets">
      <h2>Weather in London</h2>
      <button onClick={fetchWeather} className="fetch-button">
        Get Weather
      </button>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : weather ? (
        <div className="weather-info">
          <p>Temperature: {weather.current.temp_c}Â°C</p>
          <p>Condition: {weather.current.condition.text}</p>
        </div>
      ) : null}
    </div>
  );
};

export default APIWidgets;
