import React from 'react';
import IconContainer from './IconContainer';
import CTAButton from './CTAButton';
import HoverOverlay from './HoverOverlay';
import './ServiceCard.css';

const ServiceCard = ({
  title,
  description,
  metric,
  delay,
  serviceType,
}) => {
  console.log('ServiceCard props:', {
    title,
    description,
    metric,
    serviceType,
  }); // Log props for debugging

  return (
    <div
      className="service-card-container"
      data-aos="fade-up"
      data-aos-delay={delay}
    >
      <div className="service-card">
        <div className="card-front">
          <IconContainer serviceType={serviceType} />
          <h3>{title}</h3>
          <p>{description}</p>
          <p className="service-metric">{metric}</p>
          <CTAButton text="Learn More" />
        </div>
        <div className="card-back">
          <HoverOverlay />
          <p className="card-back-text">
            Discover how {title} can revolutionize your business.
            Click to learn more!
          </p>
          <CTAButton text="Explore Now" />
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
