import React, { useState } from 'react';

const InvestorDealCalculator = () => {
  const [purchasePrice, setPurchasePrice] = useState('');
  const [repairs, setRepairs] = useState('');
  const [arv, setArv] = useState('');
  const [dealType, setDealType] = useState('');
  const [equity, setEquity] = useState('');
  const [wholesalePrice, setWholesalePrice] = useState('');

  const handleCalculate = () => {
    const calculatedEquity =
      arv - (parseFloat(purchasePrice) + parseFloat(repairs));
    setEquity(calculatedEquity);
    setWholesalePrice(calculatedEquity * 0.7);
    if (calculatedEquity >= arv * 0.7) {
      setDealType('Wholesale');
    } else if (calculatedEquity >= arv * 0.8) {
      setDealType('Flip');
    } else {
      setDealType('Creative Finance');
    }
  };

  return (
    <div className="investor-deal-calculator">
      <div>
        <label>Purchase Price:</label>
        <input
          type="number"
          value={purchasePrice}
          onChange={(e) => setPurchasePrice(e.target.value)}
        />
      </div>
      <div>
        <label>Repair Costs:</label>
        <input
          type="number"
          value={repairs}
          onChange={(e) => setRepairs(e.target.value)}
        />
      </div>
      <div>
        <label>After Repair Value (ARV):</label>
        <input
          type="number"
          value={arv}
          onChange={(e) => setArv(e.target.value)}
        />
      </div>
      <button onClick={handleCalculate}>Analyze Deal</button>
      {equity && (
        <div>
          <h3>Equity: ${equity}</h3>
          <h3>Wholesale Price: ${wholesalePrice}</h3>
          <h3>Deal Type: {dealType}</h3>
        </div>
      )}
    </div>
  );
};

export default InvestorDealCalculator;
