import React, { useEffect, useState } from 'react';
import { useTheme } from './context/ThemeContext';
import Home from './pages/Home/Home';
import ThemeSelectionModal from './components/ThemeSelectionModal/ThemeSelectionModal';
import AOS from 'aos';
import 'aos/dist/aos.css';

function App() {
  const { theme, setTheme } = useTheme();
  const [isModalVisible, setIsModalVisible] = useState(false);

  useEffect(() => {
    AOS.init({ duration: 1000 });

    // Check if a theme is already set, if not show the modal
    if (!theme) {
      setIsModalVisible(true);
    } else {
      setIsModalVisible(false);
    }
  }, [theme]);

  const handleThemeSelection = (selectedTheme) => {
    setTheme(selectedTheme);
    setIsModalVisible(false);
  };

  // Render the theme selection modal if no theme is selected
  if (isModalVisible) {
    console.log('No theme selected, showing ThemeSelectionModal');
    return <ThemeSelectionModal setTheme={handleThemeSelection} />;
  }

  return (
    <div className={`app ${theme}`}>
      <Home />
    </div>
  );
}

export default App;
