import React, { useState } from 'react';
import './NearbyFinder.css';

const NearbyFinder = () => {
  const [location, setLocation] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    // Call an external API to fetch nearby restaurants and grocery stores
    // Use placeholder data for now
    const result = [
      { name: 'Pizza Place', type: 'Restaurant' },
      { name: 'Whole Foods', type: 'Grocery Store' },
    ];
    setResults(result);
  };

  return (
    <div className="nearby-finder">
      <h3>Nearby Restaurants & Grocery Stores</h3>
      <input
        type="text"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        placeholder="Enter your location"
      />
      <button onClick={handleSearch}>Find Nearby Places</button>
      {results.length > 0 && (
        <ul>
          {results.map((place, index) => (
            <li key={index}>
              {place.name} - {place.type}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default NearbyFinder;
