import React from 'react';
import { useTheme } from '../../context/ThemeContext';
import { themeContent } from '../../data/themeContent';
import CTAButton from '../CTAButton/CTAButton';
import './HeroSection.css';

const HeroSection = () => {
  const { theme } = useTheme();
  const content = themeContent[theme]; // Get the specific content based on the selected theme

  return (
    <section className={`hero-section ${theme}`} data-aos="fade-up">
      <h1 className={`hero-text ${theme}`}>{content.heroTitle}</h1>
      <p className={`hero-description ${theme}`}>
        {content.heroDescription}
      </p>
      <CTAButton text="Get Started" theme={theme} />
    </section>
  );
};

export default HeroSection;
