import React from 'react';
import './HeroSection.css';
import ContactFormModal from '../ContactFormModal/ContactFormModal';
import { useThemeSettings } from '../../context/theme/ThemeSettingsContext/ThemeSettingsContext';
import { useState } from 'react';

const HeroSection = ({
  title,
  subtitle,
  backgroundVideo,
  ctaButton,
  align = 'center',
  isTransparent = false, // New prop to control transparency
  overlayOpacity = 0.7, // Default opacity value
}) => {
  const themeSettings = useThemeSettings();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleModalOpen = () => {
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  return (
    <section
      className={`hero-section ${align} ${
        isTransparent ? 'transparent' : ''
      }`}
      style={{
        backgroundColor: isTransparent
          ? 'transparent'
          : themeSettings.colors.background,
        color: isTransparent ? '#000' : themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
        overlayOpacity: 0.3,
      }}
    >
      {backgroundVideo && (
        <video
          className="background-video"
          autoPlay
          loop
          muted
          playsInline
        >
          <source src={backgroundVideo} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}
      <div className="hero-content">
        <h1>{title}</h1>
        <p>{subtitle}</p>
        <div className="hero-buttons">
          {ctaButton ? (
            <div
              onClick={handleModalOpen}
              className="cta-button-wrapper"
            >
              {ctaButton}
            </div>
          ) : null}
        </div>
      </div>
      {isModalOpen && <ContactFormModal onClose={handleModalClose} />}
    </section>
  );
};

export default HeroSection;
