import './SectionHeader.css';
import React from 'react';

const SectionHeader = ({
  title,
  subtitle,
  align = 'center', // Default alignment is center, but can be customized
  backgroundImage, // Optional background image
  withDecoration = true, // Option to include decorative elements
  themeSettings, // Theme settings from context
}) => {
  const backgroundColor =
    themeSettings?.colors?.background || '#ffffff'; // Fallback to white
  const textColor = themeSettings?.colors?.text || '#000000'; // Fallback to black

  return (
    <div
      className={`section-header ${align}`}
      style={{
        backgroundImage: backgroundImage
          ? `url(${backgroundImage})`
          : 'none',
        color: themeSettings.colors.text, // Ensure text color follows the theme
      }}
    >
      {withDecoration && <div className="section-decoration"></div>}
      <h2
        className="section-title"
        style={{ color: themeSettings.colors.primary }}
      >
        {title}
      </h2>
      {subtitle && (
        <p
          className="section-subtitle"
          style={{ color: themeSettings.colors.text }}
        >
          {subtitle}
        </p>
      )}
      {withDecoration && (
        <div className="section-decoration-bottom"></div>
      )}
    </div>
  );
};

export default SectionHeader;
