import React, { useEffect, useState } from 'react';
import { useTheme } from './context/ThemeContext';
import Home from './pages/Home/Home';
import ThemeSelectionModal from './components/ThemeSelectionModal/ThemeSelectionModal';
import AOS from 'aos';
import 'aos/dist/aos.css';
import './App.css';

function App() {
  const { theme, setTheme } = useTheme(); // Theme management from context
  const [isModalVisible, setIsModalVisible] = useState(false);

  // Initialize animations on scroll (AOS)
  useEffect(() => {
    AOS.init({ duration: 1000 });

    // Show the modal if no theme is selected
    setIsModalVisible(!theme);
  }, [theme]);

  // Handle theme selection and close the modal
  const handleThemeSelection = (selectedTheme) => {
    setTheme(selectedTheme);
    setIsModalVisible(false);
  };

  return (
    <div className={`app ${theme}`}>
      {/* Render the Theme Selection Modal if no theme is selected */}
      {isModalVisible ? (
        <ThemeSelectionModal setTheme={handleThemeSelection} />
      ) : (
        <Home />
      )}
    </div>
  );
}

export default App;
