import React from 'react';
import './HeroSection.css';

const HeroSection = ({ theme }) => {
  const getHeroContent = () => {
    switch (theme) {
      case 'Mafia':
        return {
          heading: "An Offer You Can't Refuse",
          subheading:
            'Get the best digital solutions for your empire.',
        };
      case 'Drug Game':
        return {
          heading: 'We Are the Digital Plug',
          subheading:
            'Supplying your business with top-notch digital services.',
        };
      case 'Adult Entertainment':
        return {
          heading: 'Keep Them Coming Back',
          subheading:
            'Entertain your audience with cutting-edge solutions.',
        };
      case 'Monopoly':
        return {
          heading: 'Own the Digital Board',
          subheading:
            'Control your industry with our strategic services.',
        };
      case 'Foodie Influencer':
        return {
          heading: 'Serving Up Digital Goodness',
          subheading: 'Delicious solutions for your online presence.',
        };
      case 'Social Media Influencer':
        return {
          heading: 'Ready to Go Viral?',
          subheading:
            'Boost your brand with our influencer-ready services.',
        };
      default:
        return {
          heading:
            'Empowering Your Business with Visionary Tech Solutions',
          subheading:
            'At TechSaaS Vision, we provide innovative, scalable web development and SaaS solutions.',
        };
    }
  };

  const { heading, subheading } = getHeroContent();

  return (
    <section
      className={`hero-section ${theme.toLowerCase()}`}
      data-aos="fade-up"
    >
      <h1>{heading}</h1>
      <p>{subheading}</p>
    </section>
  );
};

export default HeroSection;
