import React from 'react';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
import './ServiceCard.css';

const ServiceCard = ({ title, description, metric, icon }) => {
  const { colors, typography, layout } = useThemeSettings();

  console.log(icon);

  return (
    <div
      className="service-card"
      style={{
        backgroundColor: colors.background,
        borderColor: colors.accent,
        padding: layout.spacing,
        borderRadius: layout.borderRadius,
        boxShadow: `0 4px 8px ${colors.accent}`,
      }}
    >
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 24 24"
        fill="#0066cc"
      >
        <path d="M12 0L2 7v10l10 7 10-7V7L12 0zM12 2l8 5-8 5-8-5 8-5zm0 20l-8-5v-8l8 5 8-5v8l-8 5z" />
      </svg>

      {icon && (
        <svg
          src={icon}
          alt={`${title} icon`}
          className="service-card-icon"
        />
      )}
      <h3
        style={{
          color: colors.primary,
          fontFamily: typography.fontFamily,
        }}
      >
        {title}
      </h3>
      <p
        style={{ fontSize: typography.fontSize, color: colors.text }}
      >
        {description}
      </p>
      <span style={{ color: colors.secondary, fontWeight: 'bold' }}>
        {metric}
      </span>
    </div>
  );
};

export default ServiceCard;
