import React, { useState } from 'react';
import './Navigation.css';
import logo from '../../assets/techsaas-logo.webp';
import { useTheme } from '../../context/theme/ThemeContext/ThemeContext';
import { ReactComponent as DarkModeIcon } from '../../assets/icons/dark-mode-icon.svg';
import { ReactComponent as LightModeIcon } from '../../assets/icons/light-mode-icon.svg';

const Navigation = ({ toggleModal, userName }) => {
  const { setTheme, theme } = useTheme();
  const [isDarkMode, setIsDarkMode] = useState(theme === 'dark');
  const [menuActive, setMenuActive] = useState(false);

  const handleDarkModeToggle = () => {
    const newTheme = isDarkMode ? 'default' : 'dark';
    setIsDarkMode(!isDarkMode);
    setTheme(newTheme);
  };

  const handleMenuToggle = () => {
    setMenuActive(!menuActive);
  };

  return (
    <nav className={`navigation ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="nav-container">
        <div className="nav-logo">
          <a href="/">
            <img src={logo} alt="TechSaas Vision" loading="lazy" />
          </a>
        </div>

        <button
          className={`nav-toggle ${menuActive ? 'active' : ''}`}
          onClick={handleMenuToggle}
          aria-label="Toggle Menu"
        >
          <span className="hamburger"></span>
        </button>

        <ul className={`nav-links ${menuActive ? 'active' : ''}`}>
          <li>
            <span tabIndex="0">
              Welcome, {userName ? userName : 'Guest'}!
            </span>
          </li>
          <li>
            <a href="/services" tabIndex="0">
              Services
            </a>
          </li>
          <li>
            <a href="/portfolio" tabIndex="0">
              Portfolio
            </a>
          </li>
          <li>
            <a href="/about" tabIndex="0">
              About
            </a>
          </li>
          <li>
            <a href="/contact" tabIndex="0">
              Contact
            </a>
          </li>
          <li>
            <button
              className="theme-switcher-button"
              onClick={toggleModal}
              tabIndex="0"
              aria-label="Switch Theme"
            >
              Switch Theme
            </button>
          </li>
          <li>
            <button
              className="dark-mode-button"
              onClick={handleDarkModeToggle}
              tabIndex="0"
              aria-label="Toggle Dark Mode"
            >
              {isDarkMode ? <LightModeIcon /> : <DarkModeIcon />}
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navigation;
