import React, {
  createContext,
  useContext,
  useState,
  useEffect,
} from 'react';
import { themeSettings } from './themes/themeSettings';
import ThemeSelectionModal from '../components/ThemeSelectionModal/ThemeSelectionModal';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('default');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);

  useEffect(() => {
    const savedTheme = localStorage.getItem('selectedTheme');
    const savedDarkMode = localStorage.getItem('darkMode') === 'true';
    if (savedTheme) {
      setTheme(savedTheme);
    }
    setIsDarkMode(savedDarkMode);
  }, []);

  useEffect(() => {
    if (theme) {
      localStorage.setItem('selectedTheme', theme);
    }
    localStorage.setItem('darkMode', isDarkMode);
  }, [theme, isDarkMode]);

  useEffect(() => {
    console.log('Loaded themeSettings:', themeSettings);
    console.log(
      'Current theme:',
      theme,
      'Settings:',
      themeSettings[theme]
    );
  }, [theme]);

  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode);
  };

  const showModal = () => {
    setIsModalVisible(true);
  };

  const hideModal = () => {
    setIsModalVisible(false);
  };

  const handleSetTheme = (newTheme) => {
    setTheme(newTheme);
    hideModal();
  };

  const value = {
    theme,
    isDarkMode,
    themeSettings: themeSettings[theme] || themeSettings.default,
    setTheme: handleSetTheme,
    toggleDarkMode,
    showModal,
  };

  return (
    <ThemeContext.Provider value={value}>
      <div className={isDarkMode ? 'dark-mode' : 'light-mode'}>
        {children}
        {isModalVisible && (
          <ThemeSelectionModal setTheme={handleSetTheme} />
        )}
      </div>
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
