import React, { useEffect } from 'react';
import { useTheme } from '../../context/ThemeContext';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
import ServiceOverview from '../../components/ServiceOverview/ServiceOverview';
import HeroSection from '../../components/HeroSection/HeroSection';
import FooterCTA from '../../components/FooterCTA/FooterCTA';
import Testimonials from '../../components/Testimonials/Testimonials';
import PortfolioPreview from '../../components/PortfolioPreview/PortfolioPreview';
import AOS from 'aos';
import 'aos/dist/aos.css';
import './Home.css';

const Home = () => {
  const { theme } = useTheme();
  const themeSettings = useThemeSettings();

  // Ensure themeSettings is loaded and has the necessary properties
  const { colors, typography, content } = themeSettings || {};

  // Ensure content is loaded and has the necessary properties
  const { heroTitle, heroDescription, services } = content || {};

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  // Debugging logs
  console.log('Current theme:', theme);
  console.log('Theme settings:', themeSettings);
  console.log('Content:', content);

  // Handle cases where content or themeSettings is undefined
  if (!content) {
    console.log('Content not loaded, showing loading message.');
    return <div>Loading...</div>; // You can customize the loading screen as needed
  }

  return (
    <div
      className="home-page"
      style={{
        backgroundColor: colors?.background || '#ffffff',
        color: colors?.text || '#333333',
        fontFamily: typography?.fontFamily || 'Arial, sans-serif',
        fontSize: typography?.fontSize || '16px',
      }}
    >
      {/* Hero Section */}
      <HeroSection
        title={heroTitle || 'Default Hero Title'}
        description={heroDescription || 'Default Hero Description'}
      />

      {/* Service Overview Section */}
      <ServiceOverview services={services || []} />

      {/* Portfolio Preview Section */}
      <PortfolioPreview />

      {/* Testimonials Section */}
      <Testimonials />

      {/* Call to Action (CTA) Section */}
      <FooterCTA />
    </div>
  );
};

export default Home;
