import React, { useState } from 'react';
import './BasicCalculator.css';

const BasicCalculator = () => {
  const [expression, setExpression] = useState('');
  const [result, setResult] = useState('');

  const handleClick = (value) => {
    setExpression(expression + value);
  };

  const handleClear = () => {
    setExpression('');
    setResult('');
  };

  const handleCalculate = () => {
    try {
      setResult(evaluateExpression(expression));
    } catch {
      setResult('Error');
    }
  };

  const evaluateExpression = (expr) => {
    // Remove any spaces from the expression
    expr = expr.replace(/\s+/g, '');

    // Validate the expression to contain only numbers and operators
    if (!/^[0-9+\-*/().]+$/.test(expr)) {
      throw new Error('Invalid characters in expression');
    }

    // Use a safer alternative to evaluate the expression
    const safeEval = (str) => {
      return Function('"use strict";return (' + str + ')')();
    };

    return safeEval(expr);
  };

  return (
    <div className="basic-calculator">
      <input type="text" value={expression} readOnly />
      <div className="result">{result}</div>
      <div className="buttons">
        {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((num) => (
          <button
            key={num}
            onClick={() => handleClick(num.toString())}
          >
            {num}
          </button>
        ))}
        <button onClick={() => handleClick('+')}>+</button>
        <button onClick={() => handleClick('-')}>-</button>
        <button onClick={() => handleClick('*')}>*</button>
        <button onClick={() => handleClick('/')}>/</button>
        <button onClick={handleClear}>Clear</button>
        <button onClick={handleCalculate}>Calculate</button>
      </div>
    </div>
  );
};

export default BasicCalculator;
