import React, { useEffect } from 'react';
import './Home.css';
import HeroSection from '../../../components/HeroSection/HeroSection';
import ServiceOverview from '../../../components/ServiceOverview/ServiceOverview';
import Testimonials from '../../../components/Testimonials/Testimonials';
import FooterCTA from '../../../components/FooterCTA/FooterCTA';
import { useThemeSettings } from '../../../context/theme/ThemeSettingsContext/ThemeSettingsContext';

const Home = () => {
  const themeSettings = useThemeSettings();

  const dummyTestimonials = [
    {
      text: 'TechSaas Vision transformed our online presence. Their expertise and professionalism are unmatched.',
      author: 'Jane Doe',
      avatar: 'https://randomuser.me/api/portraits/women/1.jpg',
    },
    {
      text: "Outstanding service and incredible results. I can't recommend them enough!",
      author: 'John Smith',
      avatar: 'https://randomuser.me/api/portraits/men/2.jpg',
    },
    // Add more testimonials as needed
  ];

  useEffect(() => {
    // Initialize animations or other effects if needed
  }, []);

  if (!themeSettings || !themeSettings.content) {
    return <div>Loading...</div>;
  }

  return (
    <div
      className="home-page"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
    >
      {/* Hero Section */}
      <HeroSection
        title="Empowering Your Business with Visionary Tech Solutions"
        subtitle="We provide innovative, scalable web development and SaaS solutions."
        align="center"
      />

      {/* Why TechSaaS Vision Section */}
      <section className="section">
        <h2>Your Partner in Innovation</h2>
        <p>
          At TechSaaS Vision, we're not just developersâ€”we're your
          digital architects, crafting solutions that push boundaries
          and set you apart from the competition.
        </p>
        <p>
          From web development to advanced SaaS solutions, we
          understand that every business is unique. Our mission is to
          empower you with technology that not only meets today's
          needs but also scales with tomorrow's dreams. We pride
          ourselves on delivering solutions that are as unique as your
          business, infused with innovation, reliability, and a dash
          of Texas flair.
        </p>
        <button className="cta-button">Discover How</button>
      </section>

      {/* Our Process Section */}
      <section className="section">
        <h2>From Concept to Reality</h2>
        <p>
          We take your ideas and turn them into digital masterpieces.
          With a focus on collaboration and transparency, our process
          ensures that your vision is realized at every stage. Whether
          it's building a sleek website, developing a robust SaaS
          platform, or optimizing your digital presence, we've got the
          tools, expertise, and creativity to bring your concepts to
          life.
        </p>
        <button className="cta-button">See Our Process</button>
      </section>

      {/* Testimonials */}
      <Testimonials testimonials={dummyTestimonials} />

      {/* Client Success Stories Section */}
      <section className="section">
        <h2>Success Speaks for Itself</h2>
        <p>
          Our clients' success is our greatest achievement. From
          startups to established enterprises, we've helped businesses
          across industries achieve their goals. Hear from our clients
          and see how TechSaaS Vision has transformed their digital
          landscapes, driving growth and innovation.
        </p>
        <button className="cta-button">Read Testimonials</button>
      </section>

      {/* Footer CTA */}
      <FooterCTA />
    </div>
  );
};

export default Home;
