/* ServiceCard.js */
import React, { Suspense } from 'react';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
import './ServiceCard.css';

const ServiceCard = ({
  title,
  description,
  metric,
  IconComponent,
  details = [],
  price,
  badgeIcon,
  badgeName, // New prop for badge name
}) => {
  const { colors, typography, layout } = useThemeSettings();

  return (
    <div
      className="service-card"
      style={{
        backgroundColor: colors.background,
        borderColor: colors.accent,
        padding: layout.spacing,
        borderRadius: layout.borderRadius,
        boxShadow: `0 4px 8px rgba(0, 0, 0, 0.1)`,
        transition: 'transform 0.3s ease-in-out',
        position: 'relative',
      }}
    >
      {/* Ribbon Badge */}
      <div className="ribbon-badge">
        <span>{badgeName}</span>
      </div>

      <Suspense fallback={<div>Loading Icon...</div>}>
        <IconComponent />
      </Suspense>
      <h3 style={{ color: colors.primary }}>{title}</h3>
      <p style={{ fontSize: typography.fontSize }}>{description}</p>
      {details.length > 0 && (
        <ul className="details-list">
          {details.map((detail, index) => (
            <li key={index} style={{ fontSize: typography.fontSize }}>
              {detail}
            </li>
          ))}
        </ul>
      )}
      {price && (
        <p
          className="price"
          style={{
            color: colors.accent,
            fontSize: typography.fontSize,
          }}
        >
          Starting at {price}
        </p>
      )}
      <span style={{ color: colors.secondary }}>{metric}</span>
    </div>
  );
};

export default ServiceCard;
