import React, { useState } from 'react';
import './MysteryBoxSection.css';
import Confetti from 'react-confetti';
import CTAButton from '../../components/CTAButton/CTAButton';

const MysteryBoxSection = ({ title, subtitle, onOpen }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(true);
    if (onOpen) onOpen();
  };

  return (
    <div className="gift-box-container">
      <div className={`gift-box ${isOpen ? 'open' : ''}`}>
        <div className="box">
          <div className="lid">
            <div className="bow"></div>
          </div>
          <div className="box-side"></div>
          <div className="box-front"></div>
          <div className="box-side"></div>
          <div className="box-bottom"></div>
        </div>
      </div>
      <h1>{title}</h1>
      <p>{subtitle}</p>
      {!isOpen && (
        <CTAButton text="Open Gift" onClick={handleClick} />
      )}
      {isOpen && (
        <Confetti
          width={window.innerWidth}
          height={window.innerHeight}
          numberOfPieces={200}
          recycle={false}
          gravity={0.7} // Increase gravity for faster confetti drop
        />
      )}
    </div>
  );
};

export default MysteryBoxSection;
