// Testimonials.js
import React from 'react';
import './Testimonials.css';

const Testimonials = ({ testimonials = [] }) => {
  // Check if testimonials array is valid
  if (!testimonials.length) {
    return <p>No testimonials available at the moment.</p>;
  }

  return (
    <div className="testimonials">
      {testimonials.map((testimonial, index) => (
        <div key={index} className="testimonial-card">
          <p className="testimonial-text">
            {/* Safely access and display testimonial text */}
            {testimonial.text
              ? testimonial.text.toLowerCase()
              : 'No testimonial text available.'}
          </p>
          <p className="testimonial-author">
            {/* Safely access and display testimonial author */}-{' '}
            {testimonial.author || 'Anonymous'}
          </p>
        </div>
      ))}
    </div>
  );
};

export default Testimonials;
