import './CalculatorWidget.css';
import { useState } from 'react';
import { useThemeSettings } from '../context/theme/ThemeSettingsContext/ThemeSettingsContext';

const CalculatorWidget = () => {
  const [currentValue, setCurrentValue] = useState('0');
  const [previousValue, setPreviousValue] = useState(null);
  const [operator, setOperator] = useState(null);
  const themeSettings = useThemeSettings();

  const handleButtonClick = (value) => {
    if (['+', '-', '*', '/'].includes(value)) {
      setOperator(value);
      setPreviousValue(currentValue);
      setCurrentValue('0');
    } else if (value === '=') {
      calculateResult();
    } else if (value === 'C') {
      clear();
    } else {
      setCurrentValue(
        currentValue === '0' ? value : currentValue + value
      );
    }
  };

  const calculateResult = () => {
    if (operator && previousValue !== null) {
      const result = eval(
        `${previousValue}${operator}${currentValue}`
      );
      setCurrentValue(result.toString());
      setOperator(null);
      setPreviousValue(null);
    }
  };

  const clear = () => {
    setCurrentValue('0');
    setPreviousValue(null);
    setOperator(null);
  };

  return (
    <div
      className="calculator-widget"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
      role="region"
      aria-label="Ultimate Calculator Widget"
    >
      <div className="calculator-display">
        <input
          type="text"
          value={currentValue}
          disabled
          style={{
            backgroundColor: themeSettings.colors.secondaryBackground,
            color: themeSettings.colors.text,
            border: `2px solid ${themeSettings.colors.accent}`,
          }}
        />
      </div>
      <div className="calculator-grid">
        {['7', '8', '9', '/'].map((value) => (
          <button
            key={value}
            onClick={() => handleButtonClick(value)}
            style={{
              backgroundColor:
                themeSettings.colors.secondaryBackground,
              color: themeSettings.colors.text,
            }}
          >
            {value}
          </button>
        ))}
        {['4', '5', '6', '*'].map((value) => (
          <button
            key={value}
            onClick={() => handleButtonClick(value)}
            style={{
              backgroundColor:
                themeSettings.colors.secondaryBackground,
              color: themeSettings.colors.text,
            }}
          >
            {value}
          </button>
        ))}
        {['1', '2', '3', '-'].map((value) => (
          <button
            key={value}
            onClick={() => handleButtonClick(value)}
            style={{
              backgroundColor:
                themeSettings.colors.secondaryBackground,
              color: themeSettings.colors.text,
            }}
          >
            {value}
          </button>
        ))}
        {['0', 'C', '=', '+'].map((value) => (
          <button
            key={value}
            onClick={() => handleButtonClick(value)}
            className={value === '0' ? 'span-two' : ''}
            style={{
              backgroundColor: themeSettings.colors.accent,
              color: themeSettings.colors.textOnAccent,
            }}
          >
            {value}
          </button>
        ))}
      </div>
    </div>
  );
};

export default CalculatorWidget;
