import React, { useEffect } from 'react';
import { useTheme } from '../../context/ThemeContext';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
import ServiceOverview from '../../components/ServiceOverview/ServiceOverview';
import HeroSection from '../../components/HeroSection/HeroSection';
import FooterCTA from '../../components/FooterCTA/FooterCTA';
import Testimonials from '../../components/Testimonials/Testimonials';
import PortfolioPreview from '../../components/PortfolioPreview/PortfolioPreview';
import AOS from 'aos';
import 'aos/dist/aos.css';
import './Home.css';

const Home = () => {
  const { theme } = useTheme();
  const themeSettings = useThemeSettings();

  useEffect(() => {
    AOS.init({ duration: 1000 });
    console.log('Theme changed in Home component:', theme);
  }, [theme]);

  if (!themeSettings || !themeSettings.content) {
    return <div>Loading...</div>;
  }

  const { content } = themeSettings;

  return (
    <div
      className="home-page"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
        fontSize: themeSettings.typography.fontSize,
        transition: 'all 0.3s ease', // Smooth transition for theme changes
      }}
    >
      <HeroSection
        title={content.heroTitle}
        description={content.heroDescription}
        // Apply theme-based styling directly to the HeroSection
        style={{
          color: themeSettings.colors.accent,
        }}
      />

      <ServiceOverview services={content.services} />

      <PortfolioPreview />

      <Testimonials testimonials={content.testimonials || []} />

      <FooterCTA />
    </div>
  );
};

export default Home;
