import React, { useEffect } from 'react';
import { useTheme } from '../../context/ThemeContext';
import { themeContent } from '../../data/themeContent';
import ServiceOverview from '../../components/ServiceOverview/ServiceOverview';
import HeroSection from '../../components/HeroSection/HeroSection';
import AOS from 'aos';
import 'aos/dist/aos.css';
import './Home.css';

const Home = () => {
  const { theme } = useTheme();

  // Log the current theme and themeContent for debugging
  console.log('Current theme:', theme);
  console.log('Theme content:', themeContent);

  // Add a fallback to default theme content if the current theme doesn't exist
  const content = themeContent[theme] || themeContent.default;

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  return (
    <div className={`home-page ${theme}`}>
      <HeroSection
        title={content?.heroTitle || 'Default Hero Title'}
        description={
          content?.heroDescription || 'Default Hero Description'
        }
      />
      <ServiceOverview services={content?.services || []} />
    </div>
  );
};

export default Home;
