import { useThemeSettings } from '../../context/ThemeContext'; // Import the theme settings
import './ThemeSelectionModal.css';

const ThemeSelectionModal = ({ setTheme }) => {
  const themeSettings = useThemeSettings();
  const themes = Object.keys(themeSettings); // Extract theme names dynamically

  const handleThemeSelection = (selectedTheme) => {
    setTheme(selectedTheme);
  };

  return (
    <div className="theme-selection-modal">
      <h2>Select Your Theme</h2>
      <div className="theme-options">
        {themes.map((theme) => (
          <button
            key={theme}
            onClick={() => handleThemeSelection(theme)}
            className="theme-button"
          >
            {theme.charAt(0).toUpperCase() + theme.slice(1)}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ThemeSelectionModal;
