import React from 'react';
import { useThemeSettings } from '../../context/theme/ThemeSettingsContext/ThemeSettingsContext';
import './Button.css';

const Button = ({ label, onClick, disabled = false }) => {
  const themeSettings = useThemeSettings();

  return (
    <button
      className="button"
      onClick={onClick}
      disabled={disabled}
      style={{
        backgroundColor: themeSettings.colors.buttonBackground,
        color: themeSettings.colors.buttonText,
        borderColor: themeSettings.colors.buttonBorderColor,
      }}
    >
      {label}
    </button>
  );
};

export default Button;
