import React from 'react';
import './HeroSection.css';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
import videoSource from '../../assets/videos/logovideo.mp4'; // Import your video

const HeroSection = ({
  title,
  description,
  align = 'center',
  buttons = [],
}) => {
  const themeSettings = useThemeSettings();

  return (
    <section className={`hero-section ${align}`}>
      {/* Video Background */}
      <video autoPlay loop muted className="hero-video-background">
        <source src={videoSource} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Overlay Content */}
      <div
        className="hero-content"
        style={{
          backgroundColor: themeSettings.colors.background,
          color: themeSettings.colors.text,
          fontFamily: themeSettings.typography.fontFamily,
        }}
      >
        <h1>{title}</h1>
        <p>{description}</p>
        <div className="hero-buttons">
          {buttons.map((button, index) => (
            <a
              key={index}
              href={button.href}
              className="hero-button"
              style={{ backgroundColor: themeSettings.colors.accent }}
            >
              {button.label}
            </a>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
