import React, { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css'; // Import AOS styles
import Home from './pages/Home/Home'; // Importing Home component
import ServicesPage from './pages/ServicesPage';
import PortfolioPage from './pages/PortfolioPage';
import ThemeSelectionModal from './components/ThemeSelectionModal';
import './App.css';

const App = () => {
  const [theme, setTheme] = useState(null);

  useEffect(() => {
    AOS.init({ duration: 1000 }); // Initialize AOS with your preferred settings
    AOS.refresh(); // Refresh AOS after it has been initialized

    const savedTheme = localStorage.getItem('selectedTheme');
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);

  return (
    <Router>
      <div className={`app-container ${theme}`}>
        {!theme && <ThemeSelectionModal setTheme={setTheme} />}
        {theme && (
          <Routes>
            <Route path="/" element={<Home theme={theme} />} />
            <Route
              path="/services"
              element={<ServicesPage theme={theme} />}
            />
            <Route
              path="/portfolio"
              element={<PortfolioPage theme={theme} />}
            />
            {/* Add more routes as needed */}
          </Routes>
        )}
      </div>
    </Router>
  );
};

export default App;
