import React, { useEffect, useState } from 'react';
import SectionHeader from '../../components/SectionHeader/SectionHeader';
import TierSection from '../../components/TierSection/TierSection';
import ComparisonChart from '../../components/ComparisonChart/ComparisonChart';
import { useThemeSettings } from '../../context/ThemeSettingsContext';
import { servicesData } from '../../data/servicesData';
import AOS from 'aos';
import 'aos/dist/aos.css';

import './ServicesPage.css';

const ServicesPage = ({ selectedTier }) => {
  const themeSettings = useThemeSettings();
  const [currentTier, setCurrentTier] = useState(selectedTier);

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  const handleTierSelect = (tier) => {
    setCurrentTier(tier);
  };

  const filteredServicesData = servicesData.find(
    (tierData) => tierData.tier === currentTier
  );

  return (
    <div
      className="services-page"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
    >
      <SectionHeader
        title={`Services for ${currentTier}`}
        subtitle={`Explore the best packages tailored for ${currentTier}`}
      />
      <div className="services-overview">
        <TierSection
          tier={filteredServicesData.tier}
          services={filteredServicesData.services}
          themeSettings={themeSettings}
        />
      </div>
      <ComparisonChart onSelectTier={handleTierSelect} />
    </div>
  );
};

export default ServicesPage;
