import React, { useState } from 'react';
import { useThemeSettings } from '../context/ThemeSettingsContext';
import './CalculatorWidget.css';

const CalculatorWidget = () => {
  const [result, setResult] = useState('');
  const [expression, setExpression] = useState('');
  const themeSettings = useThemeSettings();

  const handleCalculate = () => {
    try {
      setResult(eval(expression)); // Simple evaluation for demo purposes
    } catch {
      setResult('Error');
    }
  };

  const handleClear = () => {
    setExpression('');
    setResult('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleCalculate();
    }
  };

  return (
    <div
      className="calculator-widget"
      style={{
        backgroundColor: themeSettings.colors.background,
        color: themeSettings.colors.text,
        fontFamily: themeSettings.typography.fontFamily,
      }}
      role="region"
      aria-label="Calculator Widget"
    >
      <h2
        style={{
          color: themeSettings.colors.accent,
          borderBottom: `2px solid ${themeSettings.colors.accent}`,
        }}
      >
        Calculator
      </h2>
      <input
        type="text"
        value={expression}
        onChange={(e) => setExpression(e.target.value)}
        onKeyDown={handleKeyDown}
        aria-label="Enter your expression"
        style={{
          backgroundColor: themeSettings.colors.secondaryBackground,
          color: themeSettings.colors.text,
          border: `1px solid ${themeSettings.colors.accent}`,
          outline: `2px solid ${themeSettings.colors.accent}`,
        }}
      />
      <div className="buttons-container">
        <div className="buttons-grid">
          {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((num) => (
            <button
              key={num}
              onClick={() => setExpression(expression + num)}
              aria-label={`Number ${num}`}
              className="calculator-button"
              style={{
                backgroundColor: themeSettings.colors.accent,
                color: themeSettings.colors.textOnAccent,
              }}
            >
              {num}
            </button>
          ))}
          <button
            onClick={() => setExpression(expression + '+')}
            aria-label="Add"
            className="calculator-button"
            style={{
              backgroundColor: themeSettings.colors.accent,
              color: themeSettings.colors.textOnAccent,
            }}
          >
            +
          </button>
          <button
            onClick={() => setExpression(expression + '-')}
            aria-label="Subtract"
            className="calculator-button"
            style={{
              backgroundColor: themeSettings.colors.accent,
              color: themeSettings.colors.textOnAccent,
            }}
          >
            -
          </button>
          <button
            onClick={() => setExpression(expression + '*')}
            aria-label="Multiply"
            className="calculator-button"
            style={{
              backgroundColor: themeSettings.colors.accent,
              color: themeSettings.colors.textOnAccent,
            }}
          >
            *
          </button>
          <button
            onClick={() => setExpression(expression + '/')}
            aria-label="Divide"
            className="calculator-button"
            style={{
              backgroundColor: themeSettings.colors.accent,
              color: themeSettings.colors.textOnAccent,
            }}
          >
            /
          </button>
        </div>
        <div className="action-buttons">
          <button
            onClick={handleClear}
            aria-label="Clear"
            className="clear-button"
            style={{
              backgroundColor:
                themeSettings.colors.secondaryBackground,
              color: themeSettings.colors.text,
              border: `1px solid ${themeSettings.colors.accent}`,
            }}
          >
            Clear
          </button>
          <button
            onClick={handleCalculate}
            aria-label="Calculate"
            className="calculate-button"
            style={{
              backgroundColor: themeSettings.colors.accent,
              color: themeSettings.colors.textOnAccent,
            }}
          >
            Calculate
          </button>
        </div>
        <p className="result" aria-live="polite">
          {result}
        </p>
      </div>
    </div>
  );
};

export default CalculatorWidget;
