import React, { useState } from 'react';
import './ThemeSelectionModal.css';

const ThemeSelectionModal = ({ setTheme }) => {
  const [selectedTheme, setSelectedTheme] = useState(null);

  const handleThemeSelect = (theme) => {
    setSelectedTheme(theme);
  };

  const handleSubmit = () => {
    if (selectedTheme) {
      localStorage.setItem('selectedTheme', selectedTheme); // Save theme to localStorage
      setTheme(selectedTheme); // Pass the selected theme to the parent component
    }
  };

  return (
    <div className="theme-selection-modal-overlay">
      <div className="theme-selection-modal">
        <h2>Select Your Experience</h2>
        <p>
          Choose the theme that best matches your style, and we'll
          customize your experience.
        </p>
        <div className="theme-options">
          <button onClick={() => handleThemeSelect('Mafia')}>
            Mafia
          </button>
          <button onClick={() => handleThemeSelect('Drug Game')}>
            Drug Game
          </button>
          <button
            onClick={() => handleThemeSelect('Adult Entertainment')}
          >
            Adult Entertainment
          </button>
          <button onClick={() => handleThemeSelect('Monopoly')}>
            Monopoly
          </button>
          <button
            onClick={() => handleThemeSelect('Foodie Influencer')}
          >
            Foodie Influencer
          </button>
          <button
            onClick={() =>
              handleThemeSelect('Social Media Influencer')
            }
          >
            Social Media Influencer
          </button>
        </div>
        <button
          className="submit-button"
          onClick={handleSubmit}
          disabled={!selectedTheme}
        >
          Continue
        </button>
      </div>
    </div>
  );
};

export default ThemeSelectionModal;
