// src/components/CustomSection/Section.jsx
import React from 'react';
import { useThemeSettings } from '../../context/theme/ThemeSettingsContext/ThemeSettingsContext';
import './Section.css';

const Section = ({ title, content, buttonText, buttonAction }) => {
  const themeSettings = useThemeSettings();

  return (
    <section
      className="section"
      style={{
        backgroundColor: themeSettings.colors.sectionBackground,
        color: themeSettings.colors.text,
      }}
    >
      <h2 style={{ color: themeSettings.colors.heading }}>{title}</h2>
      <p>{content}</p>
      {buttonText && buttonAction && (
        <button
          className="cta-button"
          style={{
            backgroundColor: themeSettings.colors.accent,
            color: themeSettings.colors.buttonText,
          }}
          onClick={buttonAction}
        >
          {buttonText}
        </button>
      )}
    </section>
  );
};

export default Section;
